contracts/ArbitrageV3.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "./interfaces/IVault.sol";
import "./interfaces/IFlashLoanRecipient.sol";

contract ArbitrageV3 is IFlashLoanRecipient {
    IVault public immutable balancerVault;
    ISwapRouter public immutable uRouter; // Uniswap V3
    ISwapRouter public immutable sRouter; // Sushiswap V3
    address public immutable owner;

    // MODIFICATION: Removed hardcoded feeTier constant. It will now be a parameter.

    constructor(address _balancerVault, address _uRouter, address _sRouter) {
        balancerVault = IVault(_balancerVault);
        uRouter = ISwapRouter(_uRouter);
        sRouter = ISwapRouter(_sRouter);
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    // MODIFICATION: Added _feeTier and _amountOutMinimum as parameters for flexibility and security.
    function executeTrade(
        bool _startOnUniswap,
        address _token0,
        address _token1,
        uint24 _feeTier,
        uint256 _flashAmount,
        uint256 _amountOutMinimum
    ) external onlyOwner {
        // MODIFICATION: Pass new parameters into the userData for the callback.
        bytes memory userData = abi.encode(_startOnUniswap, _token0, _token1, _feeTier, _amountOutMinimum);
        IERC20[] memory tokens = new IERC20[](1);
        tokens[0] = IERC20(_token0);
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = _flashAmount;
        balancerVault.flashLoan(address(this), tokens, amounts, userData);
    }

    function receiveFlashLoan(
        IERC20[] memory tokens,
        uint256[] memory amounts,
        uint256[] memory feeAmounts,
        bytes memory userData
    ) external override {
        require(msg.sender == address(balancerVault), "Unauthorized callback");

        uint256 flashAmount = amounts[0];
        uint256 feeAmount = feeAmounts[0];
        uint256 amountToRepay = flashAmount + feeAmount;

        // MODIFICATION: Decode the new parameters.
        (bool startOnUniswap, address token0, address token1, uint24 feeTier, uint256 amountOutMinimum) = abi.decode(
            userData,
            (bool, address, address, uint24, uint256)
        );

        // Perform arbitrage swaps
        uint256 amountReceivedAfterSwaps;
        if (startOnUniswap) {
            // First swap on Uniswap with slippage protection
            uint256 amountAfterFirstSwap = _swap(uRouter, token0, token1, feeTier, flashAmount, amountOutMinimum);
            // Second swap on Sushiswap, requiring at least enough to repay the loan
            amountReceivedAfterSwaps = _swap(sRouter, token1, token0, feeTier, amountAfterFirstSwap, amountToRepay);
        } else {
            // First swap on Sushiswap with slippage protection
            uint256 amountAfterFirstSwap = _swap(sRouter, token0, token1, feeTier, flashAmount, amountOutMinimum);
            // Second swap on Uniswap, requiring at least enough to repay the loan
            amountReceivedAfterSwaps = _swap(uRouter, token1, token0, feeTier, amountAfterFirstSwap, amountToRepay);
        }

        // MODIFICATION: Correct profitability check BEFORE repayment.
        require(amountReceivedAfterSwaps >= amountToRepay, "Arbitrage not profitable");

        // Repay flash loan with fees
        TransferHelper.safeApprove(token0, address(balancerVault), amountToRepay);
        TransferHelper.safeTransfer(token0, address(balancerVault), amountToRepay);

        // Transfer profits to owner
        uint256 profit = amountReceivedAfterSwaps - amountToRepay;
        if (profit > 0) {
            TransferHelper.safeTransfer(token0, owner, profit);
        }
    }

    // MODIFICATION: Created a single, reusable _swap function.
    function _swap(
        ISwapRouter _router,
        address _tokenIn,
        address _tokenOut,
        uint24 _fee,
        uint256 _amountIn,
        uint256 _amountOutMinimum
    ) internal returns (uint256 amountOut) {
        TransferHelper.safeApprove(_tokenIn, address(_router), _amountIn);
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
            tokenIn: _tokenIn,
            tokenOut: _tokenOut,
            fee: _fee,
            recipient: address(this),
            deadline: block.timestamp, // Use current block timestamp
            amountIn: _amountIn,
            amountOutMinimum: _amountOutMinimum,
            sqrtPriceLimitX96: 0
        });
        amountOut = _router.exactInputSingle(params);
    }
}

